cy.request(`${API_BASE_URL}/health`).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property('status', 'OK');
  expect(response.body).to.have.property('timestamp');
});

cy.request({
  method: 'POST',
  url: `${API_BASE_URL}/login`,
  body: {
    email: 'user@test.com',
    password: 'Password123!'
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property('success', true);
  expect(response.body).to.have.property('message', 'Login successful');
  // Check token is returned
  expect(response.body).to.have.property('token');
  expect(response.body.token).to.be.a('string');
  expect(response.body.token.length).to.be.greaterThan(0);
  expect(response.body.user).to.have.property('email', 'user@test.com');
});

// First request triggers MFA requirement
cy.request({
  method: 'POST',
  url: `${API_BASE_URL}/login`,
  body: {
    email: 'mfa@test.com',
    password: 'Password123!'
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.success).to.be.false;
  expect(response.body.requiresMfa).to.be.true;
  expect(response.body.message).to.include('MFA code');
});
// Second request with MFA code
cy.request({
  method: 'POST',
  url: `${API_BASE_URL}/login`,
  body: {
    email: 'mfa@test.com',
    password: 'Password123!',
    mfaCode: '123456'
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.success).to.be.true;
  expect(response.body).to.have.property('token');
  expect(response.body.token).to.be.a('string');
});

cy.request({
  method: 'POST',
  url: `${API_BASE_URL}/login`,
  body: {
    email: 'user@test.com',
    password: 'WrongPassword123!'
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(401);
  expect(response.body.success).to.be.false;
  expect(response.body.message).to.eq('Invalid email or password');
  expect(response.body).to.have.property('attempts');
  expect(response.body).to.not.have.property('token');
});

const email = `test-${Date.now()}@test.com`;
// Attempt 1-3: Wrong password
for (let i = 0; i < 3; i++) {
  cy.request({
    method: 'POST',
    url: `${API_BASE_URL}/login`,
    body: {
      email: email,
      password: 'WrongPassword'
    },
    failOnStatusCode: false
  }).then(response => {
    expect(response.status).to.eq(401);
    expect(response.body.attempts).to.eq(i + 1);
  });
}
// Attempt 4: Should require CAPTCHA
cy.request({
  method: 'POST',
  url: `${API_BASE_URL}/login`,
  body: {
    email: email,
    password: 'WrongPassword'
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(401);
  expect(response.body.requiresCaptcha).to.be.true;
});

